import { Image, Pressable, StyleSheet, Text, View } from "react-native";
import Friends from "./Friends";
import Alarms from "./Alarms";
import Graphs from "./Graphs";
import { Item, SmallItem } from "./components/Items";
import React, { Component } from "react";
import * as Location from "expo-location";

export default class Homepage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      location: null,
      errorMsg: null,
      attendanceRate: 0,
      onTimeRate: 0,
      tuitionLost: 0,
      nextClass: "",
    };
  }

  handleSendData = (user_data) => {
    fetch("http://127.0.0.1:5000/process_data", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user_data),
    })
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log("Data: ", data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  async componentDidMount() {
    const class_list = this.props.route.params.class_list;
    const login_info = this.props.route.params.login_info;
    let user_data = {
      login_info: login_info,
      class_list: class_list,
    };
  }

  render() {
    const { navigation } = this.props;

    return (
      <View style={styles.app_container}>
        <Item title="Today's Most Missed Classes" style={styles.item}></Item>
        <Item title="Your Missed Classes" style={styles.item}></Item>
        <View style={{ display: "flex", flexDirection: "row" }}>
          <SmallItem
            title="Total Tuition Lost"
            style={styles.small_item}
          ></SmallItem>
          <SmallItem title="Next Class" style={styles.small_item}></SmallItem>
        </View>
        <View style={{ display: "flex", flexDirection: "row" }}>
          <Text style={styles.attendance_rate}>97% Attendance Rate</Text>
          <Text style={styles.attendance_rate}>82% On-time Rate</Text>
        </View>
        <Taskbar navigation={navigation}></Taskbar>
      </View>
    );
  }
}

const Taskbar = ({ navigation }) => {
  return (
    <View style={styles.taskbar_container}>
      <Pressable
        onPress={() => {
          navigation.navigate(Alarms);
        }}
      >
        <Image
          source={require("../assets/alarm-clock.png")}
          style={styles.icon}
        />
      </Pressable>
      <Pressable
        onPress={() => {
          navigation.navigate(Friends);
        }}
      >
        <Image source={require("../assets/friends.png")} style={styles.icon} />
      </Pressable>
      <Pressable
        onPress={() => {
          navigation.navigate(Graphs);
        }}
      >
        <Image source={require("../assets/graphs.png")} style={styles.icon} />
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  app_container: {
    flex: 1,
    alignItems: "center",
  },

  item: {
    width: 390,
    height: 109,
    borderWidth: 2,
    borderRadius: 20,
    margin: 20,
    alignContent: "center",
    textAlign: "center",
  },

  small_item: {
    width: 190,
    height: 109,
    borderWidth: 2,
    borderRadius: 20,
    margin: 10,
    alignContent: "center",
    textAlign: "center",
  },

  attendance_rate: {
    fontSize: 20,
    fontWeight: "bold",
    color: "green",
    textAlign: "center",
    width: 150,
    paddingTop: 30,
  },

  taskbar_container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-end",
    height: 100,
    padding: 30,
    backgroundColor: "#D3D3D3",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
  },

  icon: {
    width: 40,
    height: 40,
  },
});
